/* Code for tabs */
$(document).ready(function() {
	$( ".tabbable" ).tabs();
});
/* Code for tooltips */
$(document).ready(function() {
$(".yt-tooltip").tooltip({
    position: {
        my: "center bottom",
        at: "center top-8",
    },
    show: {
        duration: "fast"
    },
    hide: {
        effect: "hide"
    }
});
});